properties.scriptContent:
param([string]$ClientId,[string]$TenantId,[string]$SubscriptionId,[string]$ResourceGroupName,[string]$WebAppName,[string]$WebAppUrl)

Write-Output "üéØ Configuring Azure AD Authentication for pre-created app registration"
Write-Output "Client ID: $ClientId"
Write-Output "Web App URL: $WebAppUrl"
Write-Output "Tenant ID: $TenantId"

try {
    # Configure App Service Authentication using the pre-created Azure AD app
    Write-Output "Configuring App Service Authentication..."

    $headers = @{
        'Authorization' = "Bearer $((Get-AzAccessToken).Token)"
        'Content-Type' = 'application/json'
    }

    # Configure AuthSettingsV2 with the pre-created app details
    $authUri = "https://management.azure.com/subscriptions/$SubscriptionId/resourceGroups/$ResourceGroupName/providers/Microsoft.Web/sites/$WebAppName/config/authsettingsV2?api-version=2023-01-01"
    $authConfig = @{
        properties = @{
            platform = @{
                enabled = $true
            }
            globalValidation = @{
                requireAuthentication = $true
                unauthenticatedClientAction = 'RedirectToLoginPage'
                redirectToProvider = 'azureactivedirectory'
            }
            identityProviders = @{
                azureActiveDirectory = @{
                    enabled = $true
                    registration = @{
                        clientId = $ClientId
                        clientSecretSettingName = 'AZURE_CLIENT_SECRET'
                        openIdIssuer = "https://sts.windows.net/$TenantId/"
                    }
                    validation = @{
                        defaultAuthorizationPolicy = @{
                            allowedApplications = @()
                        }
                    }
                }
            }
            login = @{
                tokenStore = @{
                    enabled = $true
                }
            }
        }
    }

    $authBody = $authConfig | ConvertTo-Json -Depth 10
    Invoke-RestMethod -Uri $authUri -Method PUT -Headers $headers -Body $authBody -ErrorAction Stop

    Write-Output "‚úÖ App Service Authentication configured successfully"

    # Now update the Azure AD app registration with the redirect URIs
    Write-Output "Updating Azure AD app registration with redirect URIs..."

    # Get Microsoft Graph token for updating the app registration
    try {
        $graphTokenResponse = Invoke-RestMethod -Uri "https://login.microsoftonline.com/$TenantId/oauth2/v2.0/token" -Method Post -Body @{
            client_id = $ClientId
            scope = 'https://graph.microsoft.com/.default'
            grant_type = 'client_credentials'
            client_secret = $env:AZURE_CLIENT_SECRET
        } -ContentType 'application/x-www-form-urlencoded'

        $graphToken = $graphTokenResponse.access_token
        Write-Output "‚úÖ Successfully obtained Microsoft Graph token"

        # Get the application details
        $appsResponse = Invoke-RestMethod -Uri "https://graph.microsoft.com/v1.0/applications?`$filter=appId eq '$ClientId'" -Headers @{'Authorization' = "Bearer $graphToken"} -Method Get

        if ($appsResponse.value.Count -gt 0) {
            $app = $appsResponse.value[0]
            Write-Output "Found application: $($app.displayName)"

            # Prepare redirect URIs
            $redirectUris = @(
                "$WebAppUrl/.auth/login/aad/callback",
                "$WebAppUrl"
            )

            # Update the application with redirect URIs
            $updateBody = @{
                web = @{
                    redirectUris = $redirectUris
                    implicitGrantSettings = @{
                        enableIdTokenIssuance = $true
                        enableAccessTokenIssuance = $false
                    }
                }
            } | ConvertTo-Json -Depth 5

            $updateResponse = Invoke-RestMethod -Uri "https://graph.microsoft.com/v1.0/applications/$($app.id)" -Method PATCH -Body $updateBody -Headers @{'Authorization' = "Bearer $graphToken"; 'Content-Type' = 'application/json'}

            Write-Output "‚úÖ Azure AD app registration updated with redirect URIs"
            Write-Output "Redirect URIs configured:"
            foreach ($uri in $redirectUris) {
                Write-Output "  - $uri"
            }
        } else {
            Write-Output "‚ö†Ô∏è Application not found, redirect URIs will need to be configured manually"
        }
    } catch {
        Write-Output "‚ö†Ô∏è Could not update redirect URIs automatically: $($_.Exception.Message)"
        Write-Output "Please manually add these redirect URIs to your Azure AD app registration:"
        Write-Output "  - $WebAppUrl/.auth/login/aad/callback"
        Write-Output "  - $WebAppUrl"
    }

    # Set output values
    $DeploymentScriptOutputs = @{}
    $DeploymentScriptOutputs['AppId'] = $ClientId
    $DeploymentScriptOutputs['TenantId'] = $TenantId
    $DeploymentScriptOutputs['WebAppUrl'] = $WebAppUrl
    $DeploymentScriptOutputs['AuthenticationConfigured'] = $true
    $DeploymentScriptOutputs['NextSteps'] = "Azure AD Authentication configured successfully. Your CoPA application is ready to use!"

    Write-Output "üéâ Azure AD Authentication Configuration Complete!"
    Write-Output "Your CoPA application is now secured with Azure AD authentication."

} catch {
    Write-Error "Failed to configure Azure AD authentication: $($_.Exception.Message)"
    throw
}

$ErrorActionPreference = "Stop"

# Get parameters from environment variables
$SearchServiceName = $env:AZURE_SEARCH_SERVICE
$SearchIndexName = $env:AZURE_SEARCH_INDEX_NAME
$SearchIndexerName = $env:AZURE_SEARCH_INDEXER_NAME
$SearchDataSourceName = $env:AZURE_SEARCH_DATA_SOURCE_NAME
$StorageAccountName = $env:STORAGE_ACCOUNT_NAME
$StorageContainerName = $env:STORAGE_CONTAINER_NAME
$ResourceGroupName = $env:RESOURCE_GROUP_NAME
$AzureOpenAIEndpoint = $env:AZURE_OPENAI_ENDPOINT
$AzureOpenAIApiKey = $env:AZURE_OPENAI_KEY
$EmbeddingDeploymentName = $env:EMBEDDING_DEPLOYMENT_NAME
$SkillsetName = $env:SKILLSET_NAME

Write-Host "=== SCRIPT PARAMETERS ==="
Write-Host "SearchServiceName: $SearchServiceName"
Write-Host "ResourceGroupName: $ResourceGroupName"
Write-Host "StorageAccountName: $StorageAccountName"
Write-Host "==========================="

# Use user-assigned managed identity for authentication
Write-Host "Using user-assigned managed identity for search components setup..."

# Get the managed identity client ID from environment
$clientId = $env:MSI_CLIENT_ID
Write-Host "Using managed identity client ID: $clientId"

# Get access token using managed identity endpoint
try {
    $tokenResponse = Invoke-RestMethod -Uri "http://169.254.169.254/metadata/identity/oauth2/token?api-version=2018-02-01&resource=https://management.azure.com/&client_id=$clientId" -Method Get -Headers @{Metadata="true"} -ErrorAction Stop
    $accessToken = $tokenResponse.access_token
    Write-Host "‚úÖ Successfully obtained access token using managed identity"
} catch {
    throw "Failed to get access token: $_"
}

$authHeaders = @{
    'Authorization' = "Bearer $accessToken"
    'Content-Type' = 'application/json'
}

# Get admin keys
$searchServiceUri = "https://management.azure.com/subscriptions/$($env:SUBSCRIPTION_ID)/resourceGroups/$ResourceGroupName/providers/Microsoft.Search/searchServices/$SearchServiceName"
try {
    $adminKeyResponse = Invoke-RestMethod -Uri "$searchServiceUri/listAdminKeys?api-version=2023-11-01" -Method Post -Headers $authHeaders -ErrorAction Stop
    $adminKey = $adminKeyResponse.primaryKey
} catch {
    throw "Failed to get admin key: $_"
}

# Get storage account key
$storageAccountUri = "https://management.azure.com/subscriptions/$($env:SUBSCRIPTION_ID)/resourceGroups/$ResourceGroupName/providers/Microsoft.Storage/storageAccounts/$StorageAccountName"
try {
    $storageKeysResponse = Invoke-RestMethod -Uri "$storageAccountUri/listKeys?api-version=2023-01-01" -Method Post -Headers $authHeaders -ErrorAction Stop
    $storageKey = $storageKeysResponse.keys[0].value
} catch {
    throw "Failed to get storage key: $_"
}

# Create data source
Write-Host "Creating data source..."
$dataSourceDefinition = @{
    name = $SearchDataSourceName
    type = 'azureblob'
    credentials = @{
        connectionString = "DefaultEndpointsProtocol=https;AccountName=$StorageAccountName;AccountKey=$storageKey;EndpointSuffix=core.windows.net"
    }
    container = @{
        name = $StorageContainerName
    }
}
$dataSourcePayload = $dataSourceDefinition | ConvertTo-Json -Depth 5
$dataSourceHeaders = @{
    'api-key' = $adminKey
    'Content-Type' = 'application/json'
}
Invoke-RestMethod -Uri "https://$SearchServiceName.search.windows.net/datasources/$SearchDataSourceName`?api-version=2024-07-01" -Method Put -Headers $dataSourceHeaders -Body $dataSourcePayload -SkipHeaderValidation

# Success message
Write-Host "Search components setup completed successfully."
