properties.scriptContent:
param([string]$AppDisplayName,[string]$WebAppUrl,[int]$ClientSecretExpirationMonths,[string]$TenantId,[string]$SubscriptionId,[string]$ResourceGroupName,[string]$WebAppName)

Write-Output "Starting automatic Azure AD App Registration setup..."
Write-Output "App Display Name: $AppDisplayName"
Write-Output "Web App URL: $WebAppUrl"
Write-Output "Tenant ID: $TenantId"

try {
    # Use existing PowerShell modules in deployment environment
    Write-Output "Using pre-existing PowerShell modules..."

    # Check what modules are available
    $availableModules = Get-Module -ListAvailable Az.*
    Write-Output "Available Az modules: $($availableModules.Name -join ', ')"

    # Import only what we need if not already loaded
    if (-not (Get-Module Az.Accounts)) {
        Import-Module Az.Accounts -Force -ErrorAction SilentlyContinue
    }
    if (-not (Get-Module Az.Resources)) {
        Import-Module Az.Resources -Force -ErrorAction SilentlyContinue
    }

    # Connect using managed identity
    Write-Output "Connecting to Azure using managed identity..."
    $null = Connect-AzAccount -Identity -ErrorAction Stop

    # Set subscription context
    $null = Set-AzContext -SubscriptionId $SubscriptionId -ErrorAction Stop

    # Create the Azure AD Application
    Write-Output "Creating Azure AD application..."
    $redirectUris = @(
        "$WebAppUrl/.auth/login/aad/callback",
        "$WebAppUrl/redirect"
    )

    $app = New-AzADApplication -DisplayName $AppDisplayName -Web -RedirectUri $redirectUris -LogoutUrl "$WebAppUrl/.auth/logout"
    Write-Output "Created Azure AD application with ID: $($app.AppId)"

    # Create service principal
    Write-Output "Creating service principal..."
    $sp = New-AzADServicePrincipal -ApplicationId $app.AppId
    Write-Output "Created service principal with ID: $($sp.Id)"

    # Create client secret
    Write-Output "Creating client secret..."
    $endDate = (Get-Date).AddMonths($ClientSecretExpirationMonths)
    $secret = New-AzADAppCredential -ApplicationId $app.AppId -EndDate $endDate -DisplayName "CoPPA Deployment Secret"
    Write-Output "Created client secret"

    # Update App Service configuration with the client ID and secret
    Write-Output "Updating App Service configuration..."
    $headers = @{
        'Authorization' = "Bearer $((Get-AzAccessToken).Token)"
        'Content-Type' = 'application/json'
    }

    # Get current app settings
    $currentSettingsUri = "https://management.azure.com/subscriptions/$SubscriptionId/resourceGroups/$ResourceGroupName/providers/Microsoft.Web/sites/$WebAppName/config/appsettings/list?api-version=2023-01-01"
    $currentSettings = Invoke-RestMethod -Uri $currentSettingsUri -Method POST -Headers $headers -ErrorAction Stop

    # Add the new settings
    $currentSettings.properties.AZURE_CLIENT_ID = $app.AppId
    $currentSettings.properties.AZURE_CLIENT_SECRET = $secret.SecretText
    $currentSettings.properties.AZURE_TENANT_ID = $TenantId

    # Update app settings
    $updateSettingsUri = "https://management.azure.com/subscriptions/$SubscriptionId/resourceGroups/$ResourceGroupName/providers/Microsoft.Web/sites/$WebAppName/config/appsettings?api-version=2023-01-01"
    $settingsBody = $currentSettings | ConvertTo-Json -Depth 10
    Invoke-RestMethod -Uri $updateSettingsUri -Method PUT -Headers $headers -Body $settingsBody -ErrorAction Stop

    Write-Output "App Service configuration updated successfully"

    # Update the AuthSettingsV2 with the client ID
    Write-Output "Updating authentication configuration..."
    $authUri = "https://management.azure.com/subscriptions/$SubscriptionId/resourceGroups/$ResourceGroupName/providers/Microsoft.Web/sites/$WebAppName/config/authsettingsV2?api-version=2023-01-01"
    $authConfig = @{
        properties = @{
            platform = @{
                enabled = $true
            }
            globalValidation = @{
                requireAuthentication = $true
                unauthenticatedClientAction = 'RedirectToLoginPage'
                redirectToProvider = 'azureactivedirectory'
            }
            identityProviders = @{
                azureActiveDirectory = @{
                    enabled = $true
                    registration = @{
                        clientId = $app.AppId
                        clientSecretSettingName = 'AZURE_CLIENT_SECRET'
                        openIdIssuer = "https://sts.windows.net/$TenantId/"
                    }
                    validation = @{
                        defaultAuthorizationPolicy = @{
                            allowedApplications = @()
                        }
                    }
                }
            }
            login = @{
                tokenStore = @{
                    enabled = $true
                }
            }
        }
    }

    $authBody = $authConfig | ConvertTo-Json -Depth 10
    Invoke-RestMethod -Uri $authUri -Method PUT -Headers $headers -Body $authBody -ErrorAction Stop

    Write-Output "Authentication configuration updated successfully"

    # Set output values
    $DeploymentScriptOutputs = @{}
    $DeploymentScriptOutputs['AppId'] = $app.AppId
    $DeploymentScriptOutputs['TenantId'] = $TenantId
    $DeploymentScriptOutputs['ServicePrincipalId'] = $sp.Id
    $DeploymentScriptOutputs['AdminConsentUrl'] = "https://login.microsoftonline.com/$TenantId/adminconsent?client_id=$($app.AppId)"
    $DeploymentScriptOutputs['NextSteps'] = "OPTIONAL: If your organization requires admin consent, visit the AdminConsentUrl to grant permissions."

    Write-Output "âœ… Azure AD App Registration completed successfully!"
    Write-Output "App ID: $($app.AppId)"
    Write-Output "Admin Consent URL (if needed): https://login.microsoftonline.com/$TenantId/adminconsent?client_id=$($app.AppId)"

} catch {
    Write-Error "Failed to create Azure AD application: $($_.Exception.Message)"
    Write-Output "PowerShell Error Details: $($_.Exception)"
    Write-Output "Error at line: $($_.InvocationInfo.ScriptLineNumber)"
    throw
}
